import React from 'react';
import { useQuery } from 'react-query';
import { metricsApi, sitesApi, alertsApi } from '../services/api';
import { OverviewMetrics, SitePerformance } from '../types';
import {
  GlobeAltIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  ClockIcon,
} from '@heroicons/react/24/outline';
import clsx from 'clsx';

const Dashboard: React.FC = () => {
  const { data: overview, isLoading: overviewLoading } = useQuery<{data: OverviewMetrics}>({
    queryKey: ['overview'],
    queryFn: () => metricsApi.getOverview(),
    refetchInterval: 30000,
  });

  const { data: sitesData, isLoading: sitesLoading } = useQuery({
    queryKey: ['sites'],
    queryFn: () => sitesApi.getAll(),
    refetchInterval: 30000,
  });

  const { data: performanceData, isLoading: performanceLoading } = useQuery<{data: SitePerformance[]}>({
    queryKey: ['site-performance'],
    queryFn: () => metricsApi.getSitePerformance(),
    refetchInterval: 60000,
  });

  const { data: alertsData, isLoading: alertsLoading } = useQuery({
    queryKey: ['alerts', { limit: 10 }],
    queryFn: () => alertsApi.getAll({ limit: 10, status: 'active' }),
    refetchInterval: 30000,
  });

  if (overviewLoading) {
    return (
      <div className="animate-pulse">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="card h-24 bg-gray-200"></div>
          ))}
        </div>
      </div>
    );
  }

  const metrics = overview?.data;

  const statCards = [
    {
      name: 'Total Sites',
      value: metrics?.sites.total || 0,
      subvalue: `${metrics?.sites.active || 0} active`,
      icon: GlobeAltIcon,
      color: 'primary',
    },
    {
      name: 'Active Alerts',
      value: metrics?.alerts.active || 0,
      subvalue: `${metrics?.alerts.total || 0} total (24h)`,
      icon: ExclamationTriangleIcon,
      color: metrics?.alerts.active ? 'error' : 'success',
    },
    {
      name: 'Availability',
      value: `${metrics?.checks.availability_percentage?.toFixed(1) || 0}%`,
      subvalue: `${metrics?.checks.successful || 0}/${metrics?.checks.total || 0} checks`,
      icon: CheckCircleIcon,
      color: (metrics?.checks.availability_percentage || 0) >= 95 ? 'success' : 'warning',
    },
    {
      name: 'Avg Response Time',
      value: `${metrics?.checks.avg_response_time?.toFixed(0) || 0}ms`,
      subvalue: 'Last 24 hours',
      icon: ClockIcon,
      color: (metrics?.checks.avg_response_time || 0) < 1000 ? 'success' : 'warning',
    },
  ];

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
        <p className="mt-2 text-gray-600">
          Overview of your monitoring system status
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {statCards.map((stat) => (
          <div key={stat.name} className="card">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <stat.icon
                  className={clsx(
                    'h-8 w-8',
                    stat.color === 'primary' && 'text-primary-600',
                    stat.color === 'success' && 'text-success-600',
                    stat.color === 'warning' && 'text-warning-600',
                    stat.color === 'error' && 'text-error-600'
                  )}
                />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">{stat.name}</p>
                <p className="text-2xl font-semibold text-gray-900">{stat.value}</p>
                <p className="text-sm text-gray-500">{stat.subvalue}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Sites Status */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="card">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Sites Status</h3>
          {sitesLoading ? (
            <div className="animate-pulse space-y-3">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-4 bg-gray-200 rounded"></div>
              ))}
            </div>
          ) : (
            <div className="space-y-3">
              {sitesData?.data.slice(0, 5).map((site: any) => (
                <div key={site.id} className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div
                      className={clsx(
                        'w-3 h-3 rounded-full',
                        site.check_status === 'online' && 'bg-success-500',
                        site.check_status === 'stale' && 'bg-warning-500',
                        site.check_status === 'offline' && 'bg-error-500'
                      )}
                    />
                    <span className="text-sm font-medium text-gray-900">
                      {site.name}
                    </span>
                  </div>
                  <div className="text-right">
                    <div className="text-sm text-gray-900">
                      {site.response_time_ms ? `${site.response_time_ms}ms` : '-'}
                    </div>
                    <div className="text-xs text-gray-500">
                      {site.availability_percentage?.toFixed(1) || 0}% uptime
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Recent Alerts */}
        <div className="card">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Recent Alerts</h3>
          {alertsLoading ? (
            <div className="animate-pulse space-y-3">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-4 bg-gray-200 rounded"></div>
              ))}
            </div>
          ) : (
            <div className="space-y-3">
              {alertsData?.data.data.length > 0 ? (
                alertsData.data.data.slice(0, 5).map((alert: any) => (
                  <div key={alert.id} className="flex items-start space-x-3">
                    <span
                      className={clsx(
                        'status-badge',
                        alert.severity === 'critical' && 'status-down',
                        alert.severity === 'warning' && 'status-warning',
                        alert.severity === 'info' && 'status-info'
                      )}
                    >
                      {alert.severity}
                    </span>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900 truncate">
                        {alert.title}
                      </p>
                      <p className="text-xs text-gray-500">
                        {new Date(alert.triggered_at).toLocaleString()}
                      </p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-sm text-gray-500">No active alerts</p>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Performance Summary */}
      {performanceData && (
        <div className="card">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Site Performance (24h)</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Site
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Availability
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Avg Response
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    P95 Response
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Checks
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {performanceData.data.map((site) => (
                  <tr key={site.id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm font-medium text-gray-900">{site.name}</div>
                      <div className="text-sm text-gray-500 truncate max-w-xs">{site.url}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span
                        className={clsx(
                          'status-badge',
                          site.availability_percentage >= 99 && 'status-up',
                          site.availability_percentage >= 95 && site.availability_percentage < 99 && 'status-warning',
                          site.availability_percentage < 95 && 'status-down'
                        )}
                      >
                        {site.availability_percentage.toFixed(2)}%
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {site.avg_response_time.toFixed(0)}ms
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {site.p95_response_time.toFixed(0)}ms
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {site.successful_checks}/{site.total_checks}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
